  // Form validation helpers
  isFieldInvalid(fieldName: string): boolean {
    const field = this.contactForm.get(fieldName);
    return !!(field && field.invalid && (field.dirty || field.touched));
  }

  getFieldError(fieldName: string): string {
    const field = this.contactForm.get(fieldName);
    if (!field || !field.errors) return '';
    
    if (field.errors['required']) return `${fieldName} is required`;
    if (field.errors['email']) return 'Please enter a valid email address';
    if (field.errors['minlength']) {
      const requiredLength = field.errors['minlength'].requiredLength;
      return `${fieldName} must be at least ${requiredLength} characters`;
    }
    return 'Invalid input';
  }

  private markFormGroupTouched(): void {
    Object.keys(this.contactForm.controls).forEach(key => {
      const control = this.contactForm.get(key);
      control?.markAsTouched();
    });
  }

  toggleFaq(index: number): void {
    this.faqs[index].expanded = !this.faqs[index].expanded;
  }
}
<div class="contact-page">
  <!-- Hero Section -->
  <section class="contact-hero section">
    <div class="container">
      <div class="section-title" data-aos="fade-up">
        <h1 class="page-title">Get In Touch</h1>
        <p class="hero-description">
          Ready to discuss your next project or explore collaboration opportunities? 
          I'd love to hear from you and learn about your vision.
        </p>
        <p class="hero-subtitle">
          Whether you're looking for a frontend developer for your team, need consultation on a project, 
          or want to discuss potential collaborations, I'm always open to new opportunities.
        </p>
      </div>
    </div>
  </section>

  <!-- Contact Form & Info -->
  <section class="contact-content section bg-gray-50">
    <div class="container">
      <div class="contact-grid">
        <!-- Contact Form -->
        <div class="contact-form-section" data-aos="fade-right">
          <div class="form-card card">
            <h2>Send Me a Message</h2>
            <form [formGroup]="contactForm" (ngSubmit)="onSubmit()" class="contact-form">
              <!-- Name Field -->
              <div class="form-group">
                <label for="name" class="form-label">Full Name *</label>
                <input
                  type="text"
                  id="name"
                  formControlName="name"
                  class="form-input"
                  [class.error]="isFieldInvalid('name')"
                  placeholder="Enter your full name"
                >
                <div class="error-message" *ngIf="isFieldInvalid('name')">
                  <i class="fas fa-exclamation-circle"></i>
                  {{ getFieldError('name') }}
                </div>
              </div>

              <!-- Email Field -->
              <div class="form-group">
                <label for="email" class="form-label">Email Address *</label>
                <input
                  type="email"
                  id="email"
                  formControlName="email"
                  class="form-input"
                  [class.error]="isFieldInvalid('email')"
                  placeholder="Enter your email address"
                >
                <div class="error-message" *ngIf="isFieldInvalid('email')">
                  <i class="fas fa-exclamation-circle"></i>
                  {{ getFieldError('email') }}
                </div>
              </div>

              <!-- Subject Field -->
              <div class="form-group">
                <label for="subject" class="form-label">Subject *</label>
                <input
                  type="text"
                  id="subject"
                  formControlName="subject"
                  class="form-input"
                  [class.error]="isFieldInvalid('subject')"
                  placeholder="What's this about?"
                >
                <div class="error-message" *ngIf="isFieldInvalid('subject')">
                  <i class="fas fa-exclamation-circle"></i>
                  {{ getFieldError('subject') }}
                </div>
              </div>

              <!-- Message Field -->
              <div class="form-group">
                <label for="message" class="form-label">Message *</label>
                <textarea
                  id="message"
                  formControlName="message"
                  class="form-textarea"
                  [class.error]="isFieldInvalid('message')"
                  placeholder="Tell me about your project or inquiry..."
                  rows="6"
                ></textarea>
                <div class="error-message" *ngIf="isFieldInvalid('message')">
                  <i class="fas fa-exclamation-circle"></i>
                  {{ getFieldError('message') }}
                </div>
              </div>

              <!-- Submit Button -->
              <button
                type="submit"
                class="btn btn-primary submit-btn"
                [disabled]="isSubmitting"
                [class.loading]="isSubmitting"
              >
                <span *ngIf="!isSubmitting">
                  <i class="fas fa-paper-plane"></i>
                  Send Message
                </span>
                <span *ngIf="isSubmitting">
                  <i class="fas fa-spinner fa-spin"></i>
                  Sending...
                </span>
              </button>

              <!-- Success/Error Message -->
              <div 
                class="form-message"
                *ngIf="submitMessage"
                [class.success]="submitSuccess"
                [class.error]="!submitSuccess"
              >
                <i [class]="submitSuccess ? 'fas fa-check-circle' : 'fas fa-exclamation-circle'"></i>
                {{ submitMessage }}
              </div>
            </form>
          </div>
        </div>

        <!-- Contact Information -->
        <div class="contact-info-section" data-aos="fade-left">
          <div class="info-card card">
            <h2>Contact Information</h2>
            
            <div class="contact-items">
              <div 
                class="contact-item"
                *ngFor="let info of contactInfo"
              >
                <div class="contact-icon">
                  <i [class]="info.icon"></i>
                </div>
                <div class="contact-details">
                  <h4>{{ info.title }}</h4>
                  <p class="contact-value">{{ info.value }}</p>
                  <p class="contact-description">{{ info.description }}</p>
                </div>
              </div>
            </div>

            <div class="availability-notice">
              <div class="availability-status">
                <div class="status-indicator"></div>
                <span>Available for new opportunities</span>
              </div>
              <p>I'm currently open to new opportunities and freelance projects. Typical response time is within 24 hours.</p>
            </div>

            <!-- Direct Contact Options -->
            <div class="direct-contact">
              <h4>Quick Contact</h4>
              <div class="contact-buttons">
                <a href="mailto:shainy.poojari@email.com" class="contact-btn email-btn">
                  <i class="fas fa-envelope"></i>
                  Email Me
                </a>
                <a href="https://linkedin.com/in/shainy-poojari" target="_blank" class="contact-btn linkedin-btn">
                  <i class="fab fa-linkedin"></i>
                  LinkedIn
                </a>
                <a href="tel:+919876543210" class="contact-btn phone-btn">
                  <i class="fas fa-phone"></i>
                  Call Me
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>

