import { Component, OnInit, HostListener } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, RouterModule } from '@angular/router';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  isScrolled = false;
  isMobileMenuOpen = false;
  
  navItems = [
    { label: 'Home', route: '/', icon: 'fas fa-home' },
    { label: 'About', route: '/about', icon: 'fas fa-user' },
    { label: 'Experience', route: '/experience', icon: 'fas fa-briefcase' },
    { label: 'Skills', route: '/skills', icon: 'fas fa-cogs' },
    { label: 'Contact', route: '/contact', icon: 'fas fa-envelope' }
  ];

  constructor(private router: Router) {}

  ngOnInit(): void {}

  @HostListener('window:scroll', [])
  onWindowScroll() {
    this.isScrolled = window.pageYOffset > 50;
  }

  toggleMobileMenu() {
    this.isMobileMenuOpen = !this.isMobileMenuOpen;
  }

  closeMobileMenu() {
    this.isMobileMenuOpen = false;
  }

  isActiveRoute(route: string): boolean {
    if (route === '/') {
      return this.router.url === '/' || this.router.url === '/home';
    }
    return this.router.url === route;
  }
}
<nav class="navbar" [class.scrolled]="isScrolled">
  <div class="container">
    <div class="navbar-content">
      <!-- Logo -->
      <div class="navbar-brand">
        <a routerLink="/" class="brand-link">
          <span class="brand-text">Shainy V Poojari</span>
          <span class="brand-subtitle">Senior Frontend Engineer</span>
        </a>
      </div>

      <!-- Desktop Navigation -->
      <ul class="navbar-nav desktop-nav">
        <li class="nav-item" *ngFor="let item of navItems">
          <a 
            class="nav-link" 
            [routerLink]="item.route"
            routerLinkActive="active"
            [routerLinkActiveOptions]="{exact: item.route === '/'}"
          >
            <i [class]="item.icon"></i>
            <span>{{ item.label }}</span>
          </a>
        </li>
      </ul>

      <!-- Mobile Menu Button -->
      <button 
        class="mobile-menu-btn"
        (click)="toggleMobileMenu()"
        [class.active]="isMobileMenuOpen"
        aria-label="Toggle mobile menu"
      >
        <span class="hamburger-line"></span>
        <span class="hamburger-line"></span>
        <span class="hamburger-line"></span>
      </button>
    </div>

    <!-- Mobile Navigation -->
    <div class="mobile-nav" [class.active]="isMobileMenuOpen">
      <ul class="mobile-nav-list">
        <li class="mobile-nav-item" *ngFor="let item of navItems">
          <a 
            class="mobile-nav-link"
            [routerLink]="item.route"
            routerLinkActive="active"
            [routerLinkActiveOptions]="{exact: item.route === '/'}"
            (click)="closeMobileMenu()"
          >
            <i [class]="item.icon"></i>
            <span>{{ item.label }}</span>
          </a>
        </li>
      </ul>
    </div>
  </div>
</nav>

<!-- Mobile Menu Overlay -->
<div 
  class="mobile-overlay" 
  [class.active]="isMobileMenuOpen"
  (click)="closeMobileMenu()"
></div>
